import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import taskData from './tasks.json';


  function Square(props) {
    console.log(taskData);  //nur Test Props
    return (
      <button /*style ={{backgroundColor: "red"}}*/ id={props.id} className={"square"} onClick={props.onClick}>
        {props.value}
      </button>
    );


  }



  class Board extends React.Component {
    renderSquare(i) {
      return (
        <Square 
          value={this.props.squares[i]}
          onClick={() => this.props.onClick(i)}
          id={"square_" + i}
        />
      );
    }

  /*  render() {
      return (
        <>
        <div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
        </>
      );
    }
*/
      render() {  
          let boardSquares = [];
          for(let row = 0; row < 3; row++){
            let boardRow = [];
            for(let col = 0; col < 3; col++){
              boardRow.push(<span key={(row * 3) + col}>{this.renderSquare((row * 3) + col)}</span>);
            }
            boardSquares.push(<div className="board-row" key={row}>{boardRow}</div>);
          }

          return (
            <div>
              {boardSquares}
            </div>
          );
      }

  }

  class Game extends React.Component {
    constructor(props) {
      super(props);
      this.winner_line = null;
      this.isDraw = false;
      this.state = {
        history: [
          {
            squares: Array(9).fill(null),
            lastMove: null,
          }
        ],
        update: false,
        stepNumber: 0,
        xIsNext: true,
        isReverse: false,
      };
    }


    handleClick(i) {
      console.log("was pressed");
      const history = this.state.history.slice(0, this.state.stepNumber + 1);
      const current = history[history.length - 1];
      const squares = current.squares.slice();
      const lastMove = [i%3, Math.floor(i/3)];



      if (calculateWinner(squares)  || squares[i] || this.isDraw) {
        return;
      }

      console.log(history.length);

      if (history.length >= 9 && current !== 9) this.isDraw = true;

      squares[i] = this.state.xIsNext ? 'X' : 'O';
      this.setState({
        history: history.concat([
          {
          squares: squares,
          lastMove: lastMove,
          }
        ]),
          stepNumber: history.length,
          xIsNext: !this.state.xIsNext,
      });
    }



    jumpTo(step) {
      if (step !== 9) this.isDraw = false;
      else if (this.winner_line === null) this.isDraw = true;
      this.setState({
        stepNumber: step,
        xIsNext: (step % 2) === 0 
      });
    }

    render() {
      let history = this.state.history;
      const current = history[this.state.stepNumber];
      const winner = calculateWinner(current.squares);

      let moves = history.map((step, move) => {
        const buttonText = move 
          ? 'Go to move #' + move + ' at position (' + history[move].lastMove.toString() +')'  
          : 'Go to game start';

          return ( 
            <li key={move}>
              <button 
                className={this.state.stepNumber === move ? 'bold' : 'status'} 
                onClick={() => this.jumpTo(move)}
                //onMouseEnter={}
                //onMouseLeave={}
                >
                  {buttonText}
              </button>    
            </li>
          );
        }); 

    let status;
      if (winner) {
        status = 'Winner: ' + winner[0];

        const indices = winner[1];

        const elements = []

        elements.push(document.getElementById("square_" + indices[0]));
        elements.push(document.getElementById("square_" + indices[1]));
        elements.push(document.getElementById("square_" + indices[2]));

        for (const element of elements) {
          element.classList.add("winner");
        }

        this.winner_line = winner[1];

      } else if (this.isDraw) {
        status = "This is a draw"
      } else {
        status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        if (this.winner_line !== null){
          const elements = []

          elements.push(document.getElementById("square_" + this.winner_line[0]));
          elements.push(document.getElementById("square_" + this.winner_line[1]));
          elements.push(document.getElementById("square_" + this.winner_line[2]));

          for (const element of elements) {
            element.classList.remove("winner");
          }
          this.winner_line = null;
        }
        const winnerElements = document.getElementsByClassName("winner")? document.getElementsByClassName("winner") : null;
        if (winnerElements !== null) {
          for (const element in winnerElements) {
            element.classList?.remove("winner");
          }
        }
      }

      return (
        <div className="game">
          <div className="game-board">
            <Board 
              squares={current.squares}
              onClick={i => this.handleClick(i)}
            />
          </div>
          <div className="game-info">
            <div>{status}</div>
            <ol>{this.state.isReverse ? moves.reverse() : moves}</ol>
          </div>
          <button id='sortButton' style = {{height: 22, fontWeight: 'bold'}} hidden={!(moves.length > 1)} onClick={() => {
            this.setState({
              isReverse: !this.state.isReverse,
            })
            }}>⇅ sort me ⇅</button>
        </div>
      );
    }
  }
  // ========================================

  const root = ReactDOM.createRoot(document.getElementById("root"));
  root.render(<Game />);

  function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return [squares[a], lines[i]];
    }
  }
  return null;



}
